(function() {
  var json2yaml, yaml2json;

  yaml2json = function(pgmArgs, cb) {
    var impl;
    impl = require('./yaml2json/main');
    return impl(pgmArgs, cb);
  };

  json2yaml = function(pgmArgs, cb) {
    json2yaml = require('./json2yaml/main');
    return json2yaml(pgmArgs, cb);
  };

  module.exports = {
    yaml2json: yaml2json,
    json2yaml: json2yaml
  };

}).call(this);
